mapper.update:
	UPDATE cook

	cook_stock   = cook_stock  - #{cookStatus}

		WHERE cook_no=#{cookNo}

UPDATE cook

	cook_stock   = cook_stock  + #{cookStatus}

	WHERE cook_no=#{cookNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT * FROM apply
applier_id=#{applierId}
ORDER BY apply_no ) inner_table
WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT * FROM apply
applier_id=#{applierId}
ORDER BY apply_no ) countTable )
