mapper.update:
UPDATE product
prod_name = #{prodName} , prod_price = #{prodPrice}, prod_detail = #{prodDetail}, prod_disrate = #{prodDisRate}, prod_disprice = #{prodDisPrice}, prod_thumbnail = #{prodThumbnail}, prod_content = #{prodContent}, prod_stock = #{prodStock}, prod_theme = #{prodTheme}, prod_status = #{prodStatus}, coupon_apply = #{couponApply}
WHERE prod_no = #{prodNo}
