mapper.update:
UPDATE purchase
receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, receiver_email = #{receiverEmail}, divy_addr = #{divyAddr}, divy_message = #{divyMessage}
WHERE tran_no = #{tranNo}

UPDATE purchase

	divy_status = #{divyStatus},
	invoice_num = #{invoiceNum}

	WHERE tran_no = #{tranNo}

	UPDATE product

	prod_stock = prod_stock - #{purchaseQuantity}

	WHERE prod_no= #{prodNo}

UPDATE product

	prod_stock = prod_stock + #{purchaseQuantity}

	WHERE prod_no= #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_name, p.prod_thumbnail, p.prod_stock, p.prod_disprice, t.* FROM product p, purchase t
p.prod_no=t.prod_no(+)
AND t.tran_no IS NOT NULL
AND t.user_id=#{buyerId}
ORDER BY t.tran_no DESC ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
