mapper.select:
	SELECT user_id
	FROM users
	WHERE user_nickname = #{userNickname}

mapper.update:
UPDATE users
password = #{password} , user_name = #{userName} , user_nickname = #{userNickname} , user_birth = #{userBirth} , user_addr = #{addr} , user_phone = #{phone} , user_image = #{userImage} , mentor_apply = #{mentorApply}
WHERE user_id = #{userId}

UPDATE users
quit_status = #{quitStatus} , quit_date = SYSDATE
WHERE user_id = #{userId}
